sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//get just the date portion of the glide record

		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		// If the testdate is today, return true, else return falise
		if (today.equals(istoday)){
			return true;
		}
		else{
			return false;
		}

	},

	/*	// If the When needed date is today, do not write the record to the database.

	var today = rightnow.getLocalDate();
	var istoday = whenNeeded.getLocalDate();

	if(today == istoday){
		gs.addErrorMessage("When needed date can not be Today. Your request has not been saved to the database.");
		current.setAbortAction(true);
	}
	*/

    type: 'NeedItUtils'
};
